package week1.day2;

public class FindEvenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=5;
		
		if (num % 2 == 0) {
			System.out.println(" Given number is even" + num);
				}
		else {
			System.out.println("Given number is odd: " + num);
		}

	}

}
